mapper.select:
SELECT CONTESTNUM, CONTESTNAME, CONTESTAGENT, CONTESTSUPERVISION, CONTESUPPORT, CONTESTFIELD, CONTESTTARGET, CONTESTCOMPANY, CONTESTREWARD, CONTESTSTART, CONTESTEND, CONTESTCONTENT, CONTESTPOSTER, CONTESTSVAEFILENAME, CONTESTORIFILENAME, CONTESTPAGE, CONTESTPERSON, CONTESTPHONE, CONTESTEMAIL, MEMBERNO
FROM CONTEST
WHERE CONTESTNUM = #{contestnum}

SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT * 
					FROM CONTEST
					ORDER BY CONTESTNUM DESC 
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE CONTEST
SET CONTESTNAME = #{contestname}, CONTESTAGENT =  #{contestagent}, CONTESTSUPERVISION = #{contestsupervision}, CONTESUPPORT =  #{contesupport}, CONTESTFIELD = #{contestfield}, CONTESTTARGET = #{contesttarget}, CONTESTCOMPANY = #{contestcompany}, CONTESTREWARD = #{contestreward}, CONTESTSTART = #{conteststart}, CONTESTEND = #{contestend}, CONTESTCONTENT = #{contestcontent}, CONTESTPOSTER = #{contestposter}, CONTESTSVAEFILENAME = #{contestsvaefilename}, CONTESTORIFILENAME = #{contestorifilename}, CONTESTPAGE = #{contestpage}, CONTESTPERSON = #{contestperson}, CONTESTPHONE = #{contestphone}, CONTESTEMAIL = #{contestemail}
WHERE CONTESTNUM = #{contestnum}
